import React, { useState } from 'react';
import { Box, Button, TextField, Typography, Grid } from '@mui/material';
import { blue, green } from '@mui/material/colors';

const apikey = "318a379859ed3f676b5d60f2ad76d489";
const apikeySecret = "df3e79e1b67a35243c13cd03559d7a4c6596d7d7c63f49282c2c5ffa588e0d38";
const REDIRECT_URI = 'https://localhost:3000/wechat/callback'; // Replace with your callback URI

const Login: React.FC = () => {

  const handleOauthLogin = () => {
    const wechatAuthUrl = `https://open.weixin.qq.com/connect/qrconnect?appid=${WECHAT_APP_ID}&redirect_uri=${encodeURIComponent(REDIRECT_URI)}&response_type=code&scope=snsapi_login&state=STATE#wechat_redirect`;
    window.location.href = wechatAuthUrl;
  };

  return (
    <Box
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="center"
      height="100vh"
    >
      <Typography variant="h4" component="h1" gutterBottom>
        Login
      </Typography>
      <Box component="form" noValidate autoComplete="off">

        <Grid container spacing={2} sx={{ mt: 2 }}>

          <Grid item xs={12}>
            <Button
              variant="contained"
              sx={{ backgroundColor: blue[500], color: 'white' }}
              fullWidth
              onClick={handleOauthLogin}
            >
              UMIVERSE Oauth Login
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
};

export default Login;
