import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import CryptoJS from 'crypto-js';
import { decrypt, encrypt, restoreSecretKey } from '../utils/AES';

const secretKey = process.env.REACT_APP_API_SECRET || 'your-secret-key'; // 从环境变量获取密钥

const LoginSuccess: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const authorizationCode = queryParams.get('authorizationCode');
  const [idToken, setIdToken] = useState<string | null>(null);
  const [encryptString, setEncryptString] = useState<String>('');

  useEffect(() => {
    if (authorizationCode) {
      try {
        // 解密 authorizationCode，得到 idToken
        const decryptedData = decrypt(authorizationCode, secretKey);
        setIdToken(decryptedData);
        console.log('Decrypted idToken:', decryptedData);


        const encryptString = encrypt(decryptedData, secretKey);
        setEncryptString(encryptString);
      } catch (error) {
        console.error('Error decrypting authorizationCode:', error);
      }
    }



  }, [authorizationCode, navigate]);

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Login successful, welcome to our website!</h1>
      {authorizationCode && <p>Your authorizationCode is: {authorizationCode}</p>}
      {idToken && <p>Your idToken is: {idToken}</p>}
      {encryptString && <p>Your encryptString is: {encryptString}</p>}
    </div>
  );
};

export default LoginSuccess;
